// snapsitebug.jsx
// Renders a 'generated by...' DIV tag

const React = require('react');
const Glyphicon = require('react-bootstrap').Glyphicon;

var SnapBug = React.createClass({
    className: "SnapBug",
    render: function() {
        const theme = this.props.site.context.theme;
        const generator = this.props.generator;
        return (<div style={theme.snapBugBlock}>
               <Glyphicon glyph='cog' /> <a href="https://github.com/Encapsule/snapsite" title="View source on GitHub..." style={theme.snapBugLink}>Encapsule/{generator.agent.name}</a> v{generator.agent.version}
               </div>
               );
    }
});

module.exports = SnapBug;

